        let elements = document.querySelectorAll(".animatedGroup");
        for (let i = 0; i < elements.length; i++) {
          elements[i].classList.add("animatedGroup" + i);
          this.initAnimation(i);
        }
      },
      getRandomNumber(min, max) {
        return Math.floor(Math.random() * (max - min + 1)) + min;
      },
      initAnimation(number) {
        console.log(this.animationPositions[number]);
        // // first number between -20 and 20
        // const randomNumber1 = this.getRandomNumber(-10, 10);
        // // second number either at -20 or +20 than the first number
        // const randomNumber2 = randomNumber1 + (Math.random() < 0.5 ? -20 : 20);
        // this.animationPositions.push([randomNumber1, randomNumber2]);
        gsap.fromTo(
          ".animatedGroup" + number,
          { y: this.animationPositions[number][0] },
          {
            duration: 2,
            y: this.animationPositions[number][1],
            yoyo: true,
            repeat: -1,
            ease: "power1.inOut",
            delay: number * 0.2,
          }
        );
      },
    },
  };
</script>
